import { Descriptions, Image, Table, Typography } from 'antd';
import React from 'react';

import { winOpen } from '@/utils';

import styless from './index.less';

interface propsType {
  dataSource: any;
}

const OrderColumns: React.FC<propsType> = ({ dataSource }) => {
  const itemDescriptions = (key: string, val: any) => (
    <Descriptions.Item label={key}>
      <Typography.Paragraph
        ellipsis={{ rows: 1, tooltip: true }}
        style={{ margin: 0 }}
      >
        {val || '-'}
      </Typography.Paragraph>
    </Descriptions.Item>
  );

  return (
    <Table
      columns={[
        {
          title: '',
          dataIndex: 'itemInfo',
          width: 330,
          render: (_: any, info: any) => {
            return (
              <div className={styless.imageBox}>
                <div className={styless.imageStyle}>
                  <Image
                    src={info.itemImage}
                    width={50}
                    height={50}
                    fallback="data:image/png;base64,"
                  />
                </div>

                <div className={styless.imageTitle}>
                  <div className={styless.f}>
                    <span>商品编码：</span>
                    <span
                      className={styless.v}
                      onClick={() => winOpen(`/goods/detail/${info.itemCode}`)}
                    >
                      {info.itemCode}
                    </span>
                  </div>
                  <div className={styless.f}>
                    <span>商品ID：</span>
                    <span>{info.itemId}</span>
                  </div>
                  <div className={styless.f}>
                    <span>平台单号：</span>
                    <span>{info.orderNumber}</span>
                  </div>
                </div>
              </div>
            );
          },
        },
        {
          title: '',
          dataIndex: 'itemInfo',
          width: 200,
          render: (_: any, info: any) => {
            return (
              <Descriptions column={1}>
                {itemDescriptions('主播', info.anchorNickname)}
                {itemDescriptions('名称', info.title)}
              </Descriptions>
            );
          },
        },
        {
          title: '状态',
          dataIndex: 'itemInfo',
          width: 200,
          render: (_: any, info: any) => {
            return (
              <Descriptions column={1}>
                {itemDescriptions('规格', info.specification)}
                {itemDescriptions('数量', info.number)}
                {itemDescriptions('金额', info.money)}
              </Descriptions>
            );
          },
        },
      ]}
      showHeader={false}
      footer={undefined}
      dataSource={dataSource || []}
      rowKey="itemId"
      pagination={false}
      className={styless.tabColumns}
    />
  );
};

export default OrderColumns;
